def gerar_grafico_gantt(sequencia, atribuicoes, tempos_processamento, maquinas, operacoes_por_trabalho, total_jobs):
    # Geração de cores automaticamente para todos os jobs (50 no total)
    cores_jobs = gerar_cores_jobs(total_jobs)

    # Disponibilidade inicial de máquinas e tempo de conclusão por job
    disponibilidade_maquinas = {m: 0 for m in maquinas}
    tempo_proximo_job = {t: 0 for t in range(1, total_jobs + 1)}
    tempo_maximo_gantt = 0

    # Ajustar o tamanho da figura dinamicamente
    fig, ax = plt.subplots(figsize=(16, max(8, len(maquinas) * 0.5)))
    # Máquinas em ordem invertida para o gráfico
    maquinas_invertidas = sorted(maquinas, reverse=True)

    for op in sequencia:
        trabalho = ((op - 1) // operacoes_por_trabalho) + 1
        indice_op = ((op - 1) % operacoes_por_trabalho) + 1
        maquina = atribuicoes[op]
        tempo_proc = tempos_processamento[trabalho][indice_op][maquina]
        inicio = max(disponibilidade_maquinas[maquina], tempo_proximo_job[trabalho])
        fim = inicio + tempo_proc
        disponibilidade_maquinas[maquina] = fim
        tempo_proximo_job[trabalho] = fim

        # Atualizar o tempo máximo do gráfico (makespan)
        tempo_maximo_gantt = max(tempo_maximo_gantt, fim)

        cor = cores_jobs[trabalho]
        y_posicao = maquinas_invertidas.index(maquina)  # Obter posição no eixo y
        ax.barh(y=y_posicao, width=tempo_proc, left=inicio, color=cor, edgecolor="black")
        font_tamanho_texto = max(5, 12 - len(maquinas) // 2)
        ax.text(x=inicio + tempo_proc / 2, y=y_posicao, s=f"J{trabalho}-Op{indice_op}",
                va="center", ha="center", color="white", fontsize=font_tamanho_texto)

    # Ajustar título e os eixos
    ax.set_title(f"Gráfico de Gantt - Makespan: {tempo_maximo_gantt}", fontsize=14)
    ax.set_xlabel("Tempo", fontsize=12)
    ax.set_ylabel("Máquinas", fontsize=12)
    
    # Ajustar o eixo y para exibir corretamente as máquinas
    ax.set_yticks(range(len(maquinas)))
    ax.set_yticklabels([f"M{m}" for m in maquinas_invertidas], fontsize=8)

    # Grid e legendas
    ax.grid(which="both", linestyle="--", alpha=0.5)
    legend_labels = [mpatches.Patch(color=color, label=f"Job {job}") for job, color in cores_jobs.items()]
    ax.legend(handles=legend_labels, loc="upper right", ncol=2, fontsize=8)

    # Ajustar layout para evitar sobreposição
    plt.tight_layout()

    # Exibir o gráfico
    plt.show()


